package java_programs;

public class factorialandsum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i,fact=1,sum=0;
int number=10;
for(i=1;i<=number;i++) {
	fact=fact*i;
	sum=sum+0;
}

System.out.println("Factorial of "+number+"is: "+fact);
System.out.println(sum);
	}

}
